package com.guo.controller;import com.github.pagehelper.PageHelper;import com.github.pagehelper.PageInfo;import com.guo.entity.BlogCensorshipExample;import com.guo.entity.BlogCensorshipWithBLOBs;import com.guo.entity.BlogUserWithBLOBs;import com.guo.service.ICensorShipService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import org.springframework.web.servlet.ModelAndView;import java.util.HashMap;import java.util.List;import java.util.Map;/** * @author guoyutao * @date 2019/04/09 */@RestController@RequestMapping(value = "/censorship")public class CensorShipController  {    @Autowired    private ICensorShipService censorShipService;    @RequestMapping(value = "/find")    public ModelAndView findAll(@RequestParam(defaultValue = "1") int pageNum, @RequestParam(defaultValue = "2") int pageSize){        Map<String,Object> result =new HashMap<>();        ModelAndView model=new ModelAndView();        PageHelper.startPage(pageNum,pageSize);        BlogCensorshipExample censorshipExample=new BlogCensorshipExample();        List<BlogCensorshipWithBLOBs> censorships=censorShipService.queryAll(censorshipExample);        PageInfo<BlogCensorshipWithBLOBs> pageInfo = new PageInfo<>(censorships);        model.addObject("censorships",censorships);        model.addObject("total",pageInfo.getTotal());        model.setViewName("/sys/censorship");        return model;    }}