package com.guo.controller;import com.guo.entity.BlogUser;import com.guo.entity.SysUser;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;/** * @author guoyutao * @date 2019/02/26 */@Controllerpublic class MainController {    @RequestMapping("/tologin")    public String getLogin(Model model){        SysUser sysUser=new SysUser();        model.addAttribute("sysUser",sysUser);        return "login";    }    @RequestMapping("/index")    public String getIndex() { return  "index";}    @RequestMapping("/adminList")    public String getAdminList(Model model){        return "/sys/adminList";    }    @RequestMapping("/userList")    public String getUserList(Model model){        return "/sys/userList";    }    @RequestMapping("/hello")    public String getHello(){        return "/vue";    }}