package com.guo.service.impl;import com.guo.entity.BlogBlacklistExample;import com.guo.entity.BlogBlacklistWithBLOBs;import com.guo.mapper.BlogBlacklistMapper;import com.guo.service.IBlackListService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @author guoyutao * @date 2019/04/09 */@Servicepublic class BlackListService implements IBlackListService {    @Autowired    private BlogBlacklistMapper blacklistMapper;    @Override    public List<BlogBlacklistWithBLOBs> queryBlackAll(BlogBlacklistExample blacklistExample) {        return blacklistMapper.selectByExampleWithBLOBs(blacklistExample);    }    @Override    public void insertBlacklist(BlogBlacklistWithBLOBs blacklistWithBLOBs) {        blacklistMapper.insertSelective(blacklistWithBLOBs);    }    @Override    public void deleteBlacklist(BlogBlacklistExample blacklistExample) {        blacklistMapper.deleteByExample(blacklistExample);    }}