package com.guo.service.impl;import com.guo.entity.BlogBlogExample;import com.guo.entity.BlogBlogWithBLOBs;import com.guo.mapper.BlogBlogMapper;import com.guo.service.IBlogService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @author guoyutao * @date 2019/04/04 */@Servicepublic class BlogService implements IBlogService {    @Autowired    private BlogBlogMapper blogMapper;    @Override    public List<BlogBlogWithBLOBs> QueryAll(BlogBlogExample blogExample) {        return blogMapper.selectByExampleWithBLOBs(blogExample);    }    @Override    public void addBlog(BlogBlogWithBLOBs blogWithBLOBs) {        blogMapper.insert(blogWithBLOBs);    }    @Override    public void deleteById(String blogId) {        blogMapper.deleteByPrimaryKey(blogId);    }    @Override    public void updateById(BlogBlogWithBLOBs blogWithBLOBs) {        blogMapper.updateByPrimaryKeySelective(blogWithBLOBs);    }}