package com.guo.service.impl;import com.guo.entity.BlogCensorshipExample;import com.guo.entity.BlogCensorshipWithBLOBs;import com.guo.mapper.BlogCensorshipMapper;import com.guo.service.ICensorShipService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @author guoyutao * @date 2019/04/09 */@Servicepublic class CensorShipService implements ICensorShipService {    @Autowired    private BlogCensorshipMapper censorshipMapper;    @Override    public List<BlogCensorshipWithBLOBs> queryAll(BlogCensorshipExample example) {        return censorshipMapper.selectByExampleWithBLOBs(example);    }}