package com.guo.service.impl;import com.guo.entity.BlogFileExample;import com.guo.entity.BlogFileWithBLOBs;import com.guo.mapper.BlogFileMapper;import com.guo.service.IFileService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @author guoyutao * @date 2019/04/08 */@Servicepublic class FileService implements IFileService {    @Autowired    private BlogFileMapper fileMapper;    @Override    public List<BlogFileWithBLOBs> findFileListByBlogId(BlogFileExample fileExample) {        return fileMapper.selectByExampleWithBLOBs(fileExample);    }    @Override    public void AddFile(BlogFileWithBLOBs fileWithBLOBs) {        fileMapper.insert(fileWithBLOBs);    }}