package com.guo.service.impl;import com.guo.entity.SysUser;import com.guo.entity.SysUserExample;import com.guo.entity.SysUserWithBLOBs;import com.guo.mapper.SysUserMapper;import com.guo.service.ISysService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @author guoyutao * @date 2019/03/06 */@Servicepublic class SysService implements ISysService{    @Autowired(required=true)    private SysUserMapper sysUserMapper;    /*     * 查询管理员信息(登录用)     */    @Override    public SysUserWithBLOBs getSysUser(String username){        return sysUserMapper.selectByPrimaryKey(username);    }    @Override    public List<SysUserWithBLOBs> querySysUser(SysUserExample userExample) {        return sysUserMapper.selectByExampleWithBLOBs(userExample);    }    /*     * 添加管理员信息（注册用）     */    @Override    public int insertSysUser(SysUserWithBLOBs record){        return sysUserMapper.insertSelective(record);    }    /*     * 修改管理员信息     */    @Override    public int updateSysUser(SysUserWithBLOBs record){        return sysUserMapper.updateByPrimaryKeySelective(record);    }    /*     * 修改昵称和密码()     */    @Override    public int updataUserAndPass(SysUserWithBLOBs userWithBLOBs) {        return sysUserMapper.updateByPrimaryKeySelective(userWithBLOBs);    }    /*    * 根据权限查询管理员     */    @Override    public List<SysUserWithBLOBs> queryByPermission(String permission) {        return sysUserMapper.selectByPermission(permission);    }}