package com.guo.service.impl;import com.guo.entity.BlogUserExample;import com.guo.entity.BlogUserWithBLOBs;import com.guo.mapper.BlogUserMapper;import com.guo.service.IUserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.HashMap;import java.util.List;import java.util.Map;/** * @author guoyutao * @date 2019/04/07 */@Servicepublic class UserService implements IUserService {    @Autowired    private BlogUserMapper userMapper;    /**     * 根据ID查询User对象(登录验证)     * @param userId     * @return     */    @Override    public BlogUserWithBLOBs queryById(String userId) {        return userMapper.selectByPrimaryKey(userId);    }    /**     * 查询所有user对象     * @return     */    @Override    public List<BlogUserWithBLOBs> queryAll(BlogUserExample userExample) {        return userMapper.selectByExampleWithBLOBs(userExample);    }    @Override    public void updateByExample(BlogUserWithBLOBs userWithBLOBs, BlogUserExample userExample) {        userMapper.updateByExampleSelective(userWithBLOBs,userExample);    }}