package com.guo.utils;import java.text.SimpleDateFormat;import java.util.Date;/** * @author guoyutao * @date 2019/02/14 * 获取当前时间 */public class DateUtils {    private static final String TIME="yyyy-MM-dd HH:mm";    private static String getNowTime(){        Date now=new Date();        SimpleDateFormat dateFormat= new SimpleDateFormat(TIME);        String nowtime=dateFormat.format(now);        return nowtime;    }}